<?php  if (!defined('BASEPATH')) exit('No direct script access allowed');require_once(APPPATH . '/controllers/test/Toast.php');class TestLogin extends Toast{	//function My_test_class()	function __construct()	{		parent::__construct();		parent::Toast('login_attempts/get_attempts_num'); // Remember this				$this->load->helper(array('form', 'url'));		$this->load->library('form_validation');		$this->load->library('security');		$this->load->library('tank_auth');		$this->lang->load('tank_auth');	}			function aux_login($login, $password)	{		$remember = '';		$login_by_username = '';		$login_by_email = '';				$this->tank_auth->login($login, $password, $remember, $login_by_username, $login_by_email);	}		function test_is_max_login_attempts_exceeded()	{		$login = 'firstuser';		$password = 'lumsdssd';				for($i=0; $i<3; $i++){			$this->aux_login($login, $password);		}				$boolResult = $this->tank_auth->is_max_login_attempts_exceeded($login);		//echo $boolResult.'<br/>';		$this->_assert_equals($boolResult, 'is_true');	}	} ?>